INSERT INTO public.director (id, first_name, last_name, description) VALUES (1, 'Christopher', 'Nolan', 'Best known for his cerebral, often nonlinear, storytelling, acclaimed writer-director Christopher Nolan was born on July 30, 1970, in London, England. Over the course of 15 years of filmmaking, Nolan has gone from low-budget independent films to working on some of the biggest blockbusters ever made.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (2, 'Steven', 'Spielberg', 'One of the most influential personalities in the history of cinema, Steven Spielberg is Hollywood''s best known director and one of the wealthiest filmmakers in the world. He has an extraordinary number of commercially successful and critically acclaimed credits to his name, either as a director.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (3, 'Quentin', 'Tarantino', 'Quentin Jerome Tarantino was born in Knoxville, Tennessee. His father, Tony Tarantino, is an Italian-American actor and musician from New York, and his mother, Connie (McHugh), is a nurse from Tennessee. Quentin moved with his mother to Torrance, California, when he was four years old.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (4, 'Martin', 'Scorsese', 'Martin Charles Scorsese was born on November 17, 1942 in Queens, New York City, to Catherine Scorsese (née Cappa) and Charles Scorsese, who both worked in Manhattan''s garment district, and whose families both came from Palermo, Sicily. He was raised in the neighborhood of Little Italy.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (5, 'David', 'Fincher', 'David Fincher was born in 1962 in Denver, Colorado, and was raised in Marin County, California. When he was 18 years old he went to work for John Korty at Korty Films in Mill Valley. He subsequently worked at ILM (Industrial Light and Magic) from 1981-1983. Fincher left ILM to direct TV commercials.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (6, 'Ridley', 'Scott', 'Described by film producer Michael Deeley as "the very best eye in the business", director Ridley Scott was born on November 30, 1937 in South Shields, Tyne and Wear (then County Durham). His father was an officer in the Royal Engineers.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (7, 'Stanley', 'Kubrick', 'Stanley Kubrick was born in Manhattan, New York City, to Sadie Gertrude (Perveler) and Jacob Leonard Kubrick, a physician. His family were Jewish immigrants (from Austria, Romania, and Russia). Stanley was considered intelligent, despite poor grades at school.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (8, 'Robert', 'Zemeckis', 'A whiz-kid with special effects, Robert is from the Spielberg camp of film-making (Steven Spielberg produced many of his films).');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (9, 'Francis', 'Ford Coppola', 'Francis Ford Coppola was born in 1939 in Detroit, Michigan, but grew up in a New York suburb in a creative, supportive Italian-American family. His father, Carmine Coppola, was a composer and musician. His mother, Italia Coppola (née Pennino), had been an actress.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (10, 'Clint', 'Eastwood', 'Clint Eastwood was born May 31, 1930 in San Francisco, the son of Clinton Eastwood Sr., a bond salesman and later manufacturing executive for Georgia-Pacific Corporation, and Ruth Wood, a housewife turned IBM operator. He had a comfortable, middle-class upbringing in nearby Piedmont.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (11, 'Alfred', 'Hitchcock', 'Alfred Joseph Hitchcock was born in Leytonstone, Essex, England. He was the son of Emma Jane (Whelan; 1863 - 1942) and East End greengrocer William Hitchcock (1862 - 1914). His parents were both of half English and half Irish ancestry. He had two older siblings.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (12, 'James', 'Cameron', 'James Francis Cameron was born on August 16, 1954 in Kapuskasing, Ontario, Canada. He moved to the United States in 1971. The son of an engineer, he majored in physics at California State University before switching to English, and eventually dropping out.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (13, 'Luc', 'Besson', 'Luc Besson spent the first years of his life following his parents.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (14, 'Sergio', 'Leone', 'Sergio Leone was virtually born into the cinema - he was the son of Roberto Roberti (A.K.A. Vincenzo Leone), one of Italy''s cinema pioneers, and actress Bice Valerian. Leone entered films in his late teens, working as an assistant director to both Italian directors and U.S. directors.');
INSERT INTO public.director (id, first_name, last_name, description) VALUES (15, 'Peter', 'Jackson', 'Sir Peter Jackson made history with The Lord of the Rings trilogy, becoming the first person to direct three major feature films simultaneously. The Fellowship of the Ring, The Two Towers and The Return of the King were nominated for and collected a slew of awards from around the globe.');
